INSERT INTO `ja_options` VALUES("720", "_transient_dash_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/11/wordpress-4-4-release-candidate/\'>WordPress 4.4 Release Candidate</a> <span class=\"rss-date\">25 November 2015</span><div class=\"rssSummary\">The release candidate for WordPress 4.4 is now available. RC means we think we’re done, but with millions of users and thousands of plugins and themes, it’s possible we’ve missed something. We hope to ship WordPress 4.4 on Tuesday, December 8, but we need your help to get there. If you haven’t tested 4.4 yet, [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/12/state-of-the-word-2015/\'>Matt: State of the Word 2015</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/state-of-the-word-2015-javascript-and-api-driven-interfaces-are-the-future-of-wordpress\'>WPTavern: State of the Word 2015: JavaScript and API-driven Interfaces Are the Future of WordPress</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/state-of-the-word-2015/\'>Post Status: State of the Word, 2015</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/wpclef/\' class=\'dashboard-news-plugin-link\'>Clef Two-Factor Authentication</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=wpclef&amp;_wpnonce=a9f49df911&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Clef Two-Factor Authentication\'>Install</a>)</span></li></ul></div>", "no");
