INSERT INTO `ja_options` VALUES("986", "_transient_dash_bd94b8f41e74bae2f4dc72e9bd8379af", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://feedproxy.google.com/~r/WordpressFrancophone/~3/tgW8c7dvWys/\'>Sortie de WordPress 4.4 « Clifford »</a> <span class=\"rss-date\">9 décembre 2015</span><div class=\"rssSummary\">La version 4.4 de WordPress, baptisée “Clifford” en honneur au trompettiste de jazz Clifford Brown, est disponible en téléchargement ou en mise à jour via votre tableau de bord WordPress. Les nouvelles fonctionnalités de la 4.4 vous donnent un site plus connecté et au design adaptatif. Clifford introduit également un nouveau thème par défaut : Twenty Sixteen. Présentation [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://feedproxy.google.com/~r/feedburner/cjgL/~3/2K_79TmEaBQ/\'>Sortie de WordPress 4.4 « Clifford »</a></li><li><a class=\'rsswidget\' href=\'http://feedproxy.google.com/~r/feedburner/cjgL/~3/XCP7gZ8WS9w/\'>WordPress fait tourner 25% du web</a></li><li><a class=\'rsswidget\' href=\'http://feedproxy.google.com/~r/feedburner/cjgL/~3/Og4g9561NEU/\'>L’Hebdo WordPress n°263 du 10e anniversaire</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Extensions populaires:</span> <a href=\'https://wordpress.org/plugins/ml-slider/\' class=\'dashboard-news-plugin-link\'>Duplicator</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=ml-slider&amp;_wpnonce=432a891848&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Duplicator\'>Installer</a>)</span></li></ul></div>", "no");
INSERT INTO `ja_options` VALUES("991", "wpseo_taxonomy_meta", "a:1:{s:8:\"category\";a:6:{i:3;a:2:{s:11:\"wpseo_title\";s:44:\"Anniversaire célébrité - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:154:\"C\'est le jour de l\'anniversaire de votre célébrité préférée ? Venez jeter un oeil à ce que nous avons préparé en son honneur pour cette occasion.\";}i:4;a:2:{s:11:\"wpseo_title\";s:36:\"Dates historiques - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:146:\"Aujourd\'hui est une date historique. Et tous les jours du quotidien sont aussi des dates historiques. Découvrez l\'évènement historique du jour.\";}i:5;a:2:{s:11:\"wpseo_title\";s:31:\"Idée cadeau - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:163:\"Besoin d\'une idée cadeau ? Pour un anniversaire ou une naissance ? Découvrez nos dizaines d\'idées cadeaux parfois insolites, parfois drôles et à petits prix !\";}i:11;a:2:{s:11:\"wpseo_title\";s:41:\"Idée cadeau naissance - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:155:\"Besoin d\'une bonne idée de cadeau de naissance ? Si vous ne voulez pas offrir la sempiternelle layette, lisez nos articles d\'idées cadeaux de naissance !\";}i:12;a:2:{s:11:\"wpseo_title\";s:44:\"Idée cadeau anniversaire - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:157:\"En panne sèche d\'idée de cadeau d\'anniversaire ?Consultez notre dizaine d\'articles sur les idées cadeaux d\'anniversaire : original, insolite et pas cher !\";}i:6;a:2:{s:11:\"wpseo_title\";s:38:\"Evénement marquant - Blog Jadis\'Press\";s:10:\"wpseo_desc\";s:171:\"Quel événement marquant s\'est passé en ce jour ? La curiosité vous pique ? Découvrez l\'évévement marquant de ce jour qui a boulversé notre quotidien pour toujours.\";}}}", "yes");
