INSERT INTO `ja_options` VALUES("1540", "_transient_dash_5f25301ca0145abac6dfc3a0899dc43b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/12/clifford/\'>WordPress 4.4 “Clifford”</a> <span class=\"rss-date\">8 December 2015</span><div class=\"rssSummary\">Version 4.4 of WordPress, named “Clifford” in honor of jazz trumpeter Clifford Brown, is available for download or update in your WordPress dashboard. New features in 4.4 make your site more connected and responsive. Clifford also introduces a new default theme, Twenty Sixteen. Introducing Twenty Sixteen Our newest default theme, Twenty Sixteen, is a modern take [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/reyl-lite-a-free-ultra-minimalist-wordpress-theme\'>WPTavern: Reyl Lite: A Free Ultra Minimalist WordPress Theme</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/new-plugin-restores-lost-functionality-in-wordpress\'>WPTavern: New Plugin Restores Lost Functionality in WordPress</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/wordpress-community-growing-in-pakistan-500-attend-lahore-meetup\'>WPTavern: WordPress Community Growing in Pakistan, 500+ Attend Lahore Meetup</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/siteorigin-panels/\' class=\'dashboard-news-plugin-link\'>Page Builder by SiteOrigin</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=siteorigin-panels&amp;_wpnonce=84f66f5384&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Page Builder by SiteOrigin\'>Install</a>)</span></li></ul></div>", "no");
