INSERT INTO `ja_term_taxonomy` VALUES("3", "3", "category", "Qui fête son anniversaire aujour d’hui ? Si vou sêtes <strong>fan d’une célébrité</strong> en particulier, vous allez adorer notre rubrique qui mettra en lumière <strong>la célébrité qui fête en ce jour son anniversaire!</strong>\r\n\r\n<strong>Nous n’oublions pas non plus nos défunts</strong>, c’est en évoquant leurs souvenirs que nous les faisons vivre. Le jour de l’anniversaire de la mort d’une célébrité ne doit pas céder la place à la tristesse mais bien à <strong>une journée en son honneur</strong> !", "0", "1");
INSERT INTO `ja_term_taxonomy` VALUES("4", "4", "category", "Vous pensiez qu’aujourd’hui était un jour du quotidian comme un autre… Il n’en est rien : Découvrez <strong>ce qui s’est passé quelques années… ou quelques décennies plus tôt !</strong>\r\n\r\nVictoires, gran des batailles, défaites sportives, marriage princier, sacrement d’un roi… <strong>l’histoire et ses dates historiques</strong> vous réservent encore bien des surprises.\r\nhttp://www.jadispress.com/idee-cadeau-naissance", "0", "6");
INSERT INTO `ja_term_taxonomy` VALUES("5", "5", "category", "En panne d’inspiration ? Découvrez <strong>toutes nos idées cadeaux</strong> : de la <strong>moins chère à la plus insolite</strong> ! <span style=\"text-decoration: underline;\"><a href=\"http://www.journal-naissance-anniversaire.fr/3-journal-anniversaire\">Journaux d’anniversaire</a></span>, magazines d’antan, mugs, bouteilles de vins… et bien d’autres idées cadeaux vous attendent pour <a href=\"http://www.jadispress.com/idee-cadeau-naissance\">une naissance</a> ou <a href=\"http://www.jadispress.com/idee-cadeau-anniversaire\">un anniversaire</a>. Il ne vous restera plus que l’embarras du choix.", "0", "0");
INSERT INTO `ja_term_taxonomy` VALUES("6", "6", "category", "Les jours se succèdent et se ressemblent ?Prenez de la hauteur et découvrez les <strong>évènements marquants qui se sont passés en cejour</strong> ! Un concert inoubliable ? Un mariage de célébrité ? Une information qui a changé à jamais notre quotidien ? Découvrez <strong>l’événement marquant d’aujourd’hui.</strong>", "0", "7");
INSERT INTO `ja_term_taxonomy` VALUES("7", "7", "post_tag", "", "0", "0");
INSERT INTO `ja_term_taxonomy` VALUES("8", "8", "post_tag", "", "0", "0");
INSERT INTO `ja_term_taxonomy` VALUES("9", "9", "nav_menu", "", "0", "2");
INSERT INTO `ja_term_taxonomy` VALUES("11", "11", "category", "Un enfant est né et vousvoulez <strong>célébrersa naissance dignement</strong> ? Comment trouverune <strong>idée de cadeau de naissance qui sorte de l’ordinaire</strong> ? Oubliez la sempiternelle layette ou les jouets premier âge et offrez<strong>un cadeau vraiment marquant</strong> !<a href=\"http://www.journal-naissance-anniversaire.fr/3-journal-anniversaire\">Journaux de naissance</a>, bouteille de vin de l’année de naissance, etc.", "5", "1");
INSERT INTO `ja_term_taxonomy` VALUES("12", "12", "category", "Les jours se succèdent et se ressemblent ?Prenez de la hauteur et découvrez les <strong>évènements marquants qui se sont passés en cejour</strong> ! Un concert inoubliable ? Un mariage de célébrité ?Une information qui a changé à jamaisnotrequotidien ?Découvrez l’<strong>événement marquant d’aujour d’hui</strong>.", "5", "1");
